import React from "react";
import { Box, Heading, Flex, Text, Button, SimpleGrid, Link,Image } from "@chakra-ui/core";

const MenuItems = ({ children }) => (
    <Text mt={{ base: 4, md: 0 }} mr={6} display="block">
        {children}
    </Text>
);

const Header = props => {
    const [show, setShow] = React.useState(false);
    const handleToggle = () => setShow(!show);

    return (
        <SimpleGrid rows={2} >
            <Flex
                align="center"
                justify="center"
                wrap="wrap"
                bg="cookieBar"
                height={30}
                color="cookieBarText"
                {...props}
            >
            <Text textAlign="center" fontSize={'cookieFont'} fontFamily="brandFont">By using this site you agree to the use of cookies. To find out more see here:
                <Link color="cookieBarLink" href="#" fontWeight="bold">
                         Cookies Policy
                </Link>
            </Text>
       
            

            </Flex>
            {/* <Text textAlign="right" marginRight="35px" fontFamily="brandFont">X</Text> */}
            <Flex
                as="nav"
                justify="space-between"
                wrap="wrap"
                bg="brandBackground"
                height={200}
                color="cookieBarText"
                {...props}
            >
                <SimpleGrid columns={3}>
                <Flex mr={5} ml={70}>
                    <Image src={require('./assets/autpost_logo.jpeg')} height={110} width={370} />
                </Flex>

                <Box display={{ sm: "block", md: "none" }} onClick={handleToggle}>
                    <svg
                        fill="white"
                        width="12px"
                        viewBox="0 0 20 20"
                        xmlns="http://www.w3.org/2000/svg"
                        color="menuText"
                    >
                        <title>Menu</title>
                        <path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z" />
                    </svg>
                </Box>

                <Box
                    display={{ sm: show ? "block" : "none", md: "flex" }}
                    width={{ sm: "full", md: "auto" }}
                    alignItems="center"
                    flexGrow={1}
                    ml={70}
                    mb={70}
                >
                    <SimpleGrid columns={3} spacing={25}>
                    <MenuItems ><Text color="menuText" >Our Story</Text></MenuItems>
                    <MenuItems><Text color="menuText">Latest News</Text></MenuItems>
                    <MenuItems><Text color="menuText">Advertise With Us</Text></MenuItems>
                    </SimpleGrid>
                </Box>
                <SimpleGrid columns={2}>
                <Box
                    display={{ sm: show ? "block" : "none", md: "block" }}
                    mt={{ base: 4, md: 0 }}
                    alignItems="center"
                >
                    <Button bg="buttonColor" color="white"  width={160} height={65} mt={115} mr={25}>
                    LOG IN
        </Button>
                </Box>
                <Box
                    display={{ sm: show ? "block" : "none", md: "block" }}
                    mt={{ base: 4, md: 0 }}
                    alignItems="center"
                >
                    <Button bg="buttonColor" color="white"  width={160} height={65} mt={115} mr={115}>
                    SIGN UP
        </Button>
                </Box>
                </SimpleGrid>
                </SimpleGrid>
            </Flex>
            
        </SimpleGrid>
    );
};

export default Header;
