import React from "react";
import { Box, Heading, Flex, Text, Button, SimpleGrid, Link } from "@chakra-ui/core";

const MenuItems = ({ children }) => (
    <Text mt={{ base: 4, md: 0 }} mr={6} display="block">
        {children}
    </Text>
);

const Header = props => {
    const [show, setShow] = React.useState(false);
    const handleToggle = () => setShow(!show);

    return (
        <SimpleGrid rows={2} >
            <Flex
                align="center"
                wrap="wrap"
                padding="1.5rem"
                bg="grey"
                height={30}
                color="white"
                {...props}
            >
                <Text textAlign={'center'}>By using this site you agree to the use of cookies. To find out more see here:  <Link color="teal.500" href="#">
                        Cookies Policy
  </Link></Text>

            </Flex>
            <Flex
                as="nav"
                align="center"
                justify="space-between"
                wrap="wrap"
                padding="1.5rem"
                bg="black"
                height={200}
                color="white"
                {...props}
            >
                <Flex align="center" mr={5}>
                    <Heading as="h1" size="lg" letterSpacing={"-.1rem"} color="red">
                        Chakra UI
        </Heading>
                </Flex>

                <Box display={{ sm: "block", md: "none" }} onClick={handleToggle}>
                    <svg
                        fill="white"
                        width="12px"
                        viewBox="0 0 20 20"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <title>Menu</title>
                        <path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z" />
                    </svg>
                </Box>

                <Box
                    display={{ sm: show ? "block" : "none", md: "flex" }}
                    width={{ sm: "full", md: "auto" }}
                    alignItems="center"
                    flexGrow={1}
                >
                    <MenuItems>Docs</MenuItems>
                    <MenuItems>Examples</MenuItems>
                    <MenuItems>Blog</MenuItems>
                </Box>

                <Box
                    display={{ sm: show ? "block" : "none", md: "block" }}
                    mt={{ base: 4, md: 0 }}
                >
                    <Button bg="transparent" border="1px">
                        Create account
        </Button>
                </Box>
            </Flex>
        </SimpleGrid>
    );
};

export default Header;
